-- Cleaned PostgreSQL SchemaCREATE TABLE users (    id SERIAL PRIMARY KEY,    name VARCHAR(100),    email VARCHAR(100) NOT NULL UNIQUE,    google_id VARCHAR(255));CREATE TABLE notes (    id SERIAL PRIMARY KEY,    user_id INT REFERENCES users(id) ON DELETE CASCADE,    content TEXT NOT NULL,    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);CREATE TABLE otps (    id SERIAL PRIMARY KEY,    user_id INT REFERENCES users(id) ON DELETE CASCADE,    otp VARCHAR(6) NOT NULL,    expires_at TIMESTAMP NOT NULL);